mapper.select:
 SELECT a.*,
    (SELECT valueName FROM data_dictionary WHERE typeCode='APP_FLATFORM' AND valueId=a.flatformId) flatformName,
    (SELECT categoryName FROM app_category WHERE id=a.categoryLevel1) cname1,
    (SELECT categoryName FROM app_category WHERE id=a.categoryLevel2) cname2,
    (SELECT categoryName FROM app_category WHERE id=categoryLevel3) cname3,
    (SELECT valueName FROM data_dictionary WHERE typeCode='APP_STATUS' AND valueId=a.status) statusName,
    (SELECT versionNo FROM app_version WHERE id=a.versionId) versionName
FROM app_info a


            and a.softwareName  LIKE '%' #{softwareName} '%'


            and a.status=#{status}


            and a.flatformId=#{flatformId}


            and a.categoryLevel1=#{categoryLevel1}


            and a.categoryLevel2=#{categoryLevel2}


            and a.categoryLevel3=#{categoryLevel3}


 ORDER BY id desc
    LIMIT #{pageNo},#{pageSize}

SELECT a.*,
(SELECT valueName FROM data_dictionary WHERE typeCode='APP_FLATFORM' AND valueId=a.flatformId) flatformName,
(SELECT categoryName FROM app_category WHERE id=a.categoryLevel1) cname1,
(SELECT categoryName FROM app_category WHERE id=a.categoryLevel2) cname2,
(SELECT categoryName FROM app_category WHERE id=categoryLevel3) cname3,
(SELECT valueName FROM data_dictionary WHERE typeCode='APP_STATUS' AND valueId=a.status) statusName,
(SELECT versionNo FROM app_version WHERE id=a.versionId) versionName
FROM app_info a

    a.status=1

        and a.softwareName  LIKE '%' #{softwareName} '%'


        and a.flatformId=#{flatformId}


        and a.categoryLevel1=#{categoryLevel1}


        and a.categoryLevel2=#{categoryLevel2}


        and a.categoryLevel3=#{categoryLevel3}


ORDER BY id desc
LIMIT #{pageNo},#{pageSize}

SELECT COUNT(1) FROM app_info


       and softwareName  LIKE '%' #{softwareName} '%'


         and status=#{status}


        and flatformId=#{flatformId}


        and categoryLevel1=#{categoryLevel1}


        and categoryLevel2=#{categoryLevel2}


        and categoryLevel3=#{categoryLevel3}

SELECT COUNT(1) FROM app_info

    status=1

        and softwareName  LIKE '%' #{softwareName} '%'


        and flatformId=#{flatformId}


        and categoryLevel1=#{categoryLevel1}


        and categoryLevel2=#{categoryLevel2}


        and categoryLevel3=#{categoryLevel3}

mapper.update:
update app_info
set logoPicPath=null,logoLocPath=NULL
where id=#{id}

mapper.select:
SELECT a.*, (SELECT valueName FROM data_dictionary WHERE typeCode='APP_FLATFORM' AND valueId=a.flatformId) flatformName, (SELECT categoryName FROM app_category WHERE id=a.categoryLevel1) cname1, (SELECT categoryName FROM app_category WHERE id=a.categoryLevel2) cname2, (SELECT categoryName FROM app_category WHERE id=categoryLevel3) cname3, (SELECT valueName FROM data_dictionary WHERE typeCode='APP_STATUS' AND valueId=a.status) statusName, (SELECT versionNo FROM app_version WHERE id=a.versionId) versionName FROM app_info a
and a.id = #{id}
and a.APKName = #{APKName}

mapper.update:
update app_info
set versionId=#{versionId}
where id=#{id}
